query JobSearchByQuery($query: JobSearchQueryInput!, $consumer: String!, $offset: Int, $limit: Int, $sort: String, $trackRecent: Boolean, $searchMode: SearchMode) {
  jobSearchByQuery(
    query: $query
    consumer: $consumer
    offset: $offset
    limit: $limit
    sort: $sort
    searchMode: $searchMode
    trackRecent: $trackRecent
    returnAggregations: true
    splitBenefit: true
  ) {
    total
    searchQuery {
      guid
      body {
        ...JobSearchQueryBodySplitBenefits
        __typename
      }
      __typename
    }
    appliedFilters {
      extractedFilters {
        enabled {
          ...ExtractedFiltersEnabled
          __typename
        }
        disabled {
          ...ExtractedFiltersDisabled
          __typename
        }
        __typename
      }
      __typename
    }
    collection {
      ...JobItemResult
      __typename
    }
    aggregations {
      employmentTypes {
        ...EmploymentTypeAggregation
        __typename
      }
      careerLevels {
        ...CareerLevelAggregation
        __typename
      }
      disciplines {
        ...DisciplineAggregation
        __typename
      }
      industries {
        ...IndustryAggregation
        __typename
      }
      benefitsEmployeePerk {
        ...BenefitAggregation
        __typename
      }
      benefitsWorkingCulture {
        ...BenefitAggregation
        __typename
      }
      countries {
        ...CountryAggregation
        __typename
      }
      cities {
        ...CityAggregation
        __typename
      }
      remoteOptions {
        ...RemoteOptionAggregation
        __typename
      }
      __typename
    }
    __typename
  }
}

fragment CompanyFilter on JobFilterCompany {
  company {
    companyName
    __typename
  }
  entityId
  __typename
}

fragment EmploymentTypeFilter on JobFilterEmploymentType {
  employmentType {
    localizationValue
    __typename
  }
  entityId
  __typename
}

fragment CareerLevelFilter on JobFilterCareerLevel {
  careerLevel {
    localizationValue
    __typename
  }
  entityId
  __typename
}

fragment DisciplineFilter on JobFilterDiscipline {
  discipline {
    localizationValue
    __typename
  }
  entityId
  __typename
}

fragment IndustryFilter on JobFilterIndustry {
  industry {
    localizationValue
    __typename
  }
  entityId
  __typename
}

fragment BenefitEmployeePerkFilter on JobFilterBenefitEmployeePerk {
  benefitEmployeePerk {
    localizationValue
    __typename
  }
  entityId
  __typename
}

fragment BenefitWorkingCultureFilter on JobFilterBenefitWorkingCulture {
  benefitWorkingCulture {
    localizationValue
    __typename
  }
  entityId
  __typename
}

fragment CountryFilter on JobFilterCountry {
  country {
    localizationValue
    __typename
  }
  entityId
  __typename
}

fragment CityFilter on JobFilterCity {
  city {
    localizationValue: name
    __typename
  }
  entityId
  __typename
}

fragment SalaryFilter on JobFilterSalary {
  min
  max
  __typename
}

fragment RemoteOptionFilter on JobFilterRemoteOption {
  remoteOption {
    localizationValue
    __typename
  }
  entityId
  __typename
}

fragment JobSearchFilterCollectionSplitBenefits on JobFilterCollection {
  ...CompanyFilter
  ...EmploymentTypeFilter
  ...CareerLevelFilter
  ...DisciplineFilter
  ...IndustryFilter
  ...BenefitEmployeePerkFilter
  ...BenefitWorkingCultureFilter
  ...CountryFilter
  ...CityFilter
  ...SalaryFilter
  ...RemoteOptionFilter
  __typename
}

fragment Salary on Salary {
  currency
  amount
  __typename
}

fragment SalaryRange on SalaryRange {
  currency
  minimum
  maximum
  __typename
}

fragment SalaryEstimate on SalaryEstimate {
  currency
  minimum
  maximum
  median
  __typename
}

fragment VisibleJobCommon on VisibleJob {
  id
  slug
  url
  title
  activatedAt
  refreshedAt
  globalId
  location {
    city
    __typename
  }
  employmentType {
    localizationValue
    __typename
  }
  companyInfo {
    companyNameOverride
    company {
      id
      logos {
        x1: logo96px
        __typename
      }
      kununuData {
        ratingAverage
        __typename
      }
      __typename
    }
    __typename
  }
  salary {
    ...Salary
    ...SalaryRange
    ...SalaryEstimate
    __typename
  }
  __typename
}

fragment JobTeaserVisibleJob on VisibleJob {
  ...VisibleJobCommon
  userInteractions {
    bookmark {
      state
      __typename
    }
    __typename
  }
  __typename
}

fragment JobKeyfactV2 on JobMatchingHighlightsJobKeyfactV2 {
  __typename
  type
  localization {
    localizationValue
    __typename
  }
  localizationA11y {
    localizationValue
    __typename
  }
  ... on JobMatchingHighlightsJobKeyfactSalaryV2 {
    value {
      ...Salary
      ...SalaryRange
      ...SalaryEstimate
      __typename
    }
    __typename
  }
}

fragment JobMatchingHighlightsV2 on JobMatchingHighlightsV2 {
  token
  highlight {
    type
    localization {
      localizationValue
      __typename
    }
    localizationA11y {
      localizationValue
      __typename
    }
    __typename
  }
  matchingFacts {
    ...JobKeyfactV2
    __typename
  }
  nonMatchingFacts {
    ...JobKeyfactV2
    __typename
  }
  __typename
}

fragment JobSearchQueryBodySplitBenefits on JobSearchQueryBody {
  keywords
  location {
    text
    radius
    city {
      id
      name
      __typename
    }
    __typename
  }
  filterCollection {
    ...JobSearchFilterCollectionSplitBenefits
    __typename
  }
  __typename
}

fragment ExtractedFiltersEnabled on JobExtractedFilterEnabledCollection {
  ...CareerLevelFilter
  ...EmploymentTypeFilter
  ...SalaryFilter
  ...RemoteOptionFilter
  ... on JobFilterLocation {
    location {
      localizationValue
      radius
      __typename
    }
    __typename
  }
  __typename
}

fragment ExtractedFiltersDisabled on JobExtractedFilterDisabledCollection {
  ...CareerLevelFilter
  ...EmploymentTypeFilter
  ... on JobFilterDisabledSalary {
    salary
    __typename
  }
  ...RemoteOptionFilter
  ... on JobFilterLocation {
    location {
      localizationValue
      radius
      __typename
    }
    __typename
  }
  __typename
}

fragment JobItemResult on JobItemResult {
  trackingToken
  position
  descriptionHighlight
  jobDetail {
    ...JobTeaserVisibleJob
    __typename
  }
  matchingHighlightsV2 {
    ...JobMatchingHighlightsV2
    __typename
  }
  __typename
}

