fragment CountryAggregation on JobAggregationCountry {
  count
  id
  country {
    localizationValue
    __typename
  }
  __typename
}

fragment CityAggregation on JobAggregationCity {
  count
  id
  city {
    localizationValue: name
    __typename
  }
  __typename
}

fragment RemoteOptionAggregation on JobAggregationRemoteOption {
  id
